<?phpnamespace mer\controller;defined ('MER_EXISTS') or die('Illegal Access!!!');require_once MER_SITE . '/base/RequestRegistry.php';abstract class PageController{	private $request;		function _construct()	{		$request = \mer\base\RequestRegisrty::getRequest();		if (is_null($request))		{			$request = new Request();		}		$this->request = $request;	}		abstract function process();		function forward($resource)	{		include($resource);		exit(0);	}		function view()	{		$resource = __CLASS__;		include($resource)	}		function getRequest()	{		return $this->request;	}}